USDC_TOKEN_ADDRESS = '0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48'


def connect():
    provider = Web3(Web3.HTTPProvider(ZKSYNC_RPC_URL))
    return provider

def get_gas_price(provider):
    return provider.from_wei(provider.eth.gas_price, 'Gwei') # кол-во Wei за единицу газа
def check_balance(provider):
    checksum_address = Web3.to_checksum_address(WALLET_ADDRESS)
    balance = provider.from_wei(provider.eth.get_balance(checksum_address), 'ether')
    return balance
def swap(provider):
    router_contract = provider.eth.contract(
        address = UNISSWAP_SWAPROUTER,
        abi = CONST_ABI_CONTRACT
    )


    transaction_params = {

        'nonce': provider.eth.get_transaction_count(WALLET_ADDRESS),
        'gas': 200000,
        'gasPrice': provider.eth.gas_price,
        'from': WALLET_ADDRESS,
        'value': provider.to_wei(0.01, 'ether')
    }
    #estimate_gas = provider.eth.estimate_gas()
    #print(f'estimate gas = {estimate_gas}')
    transaction = router_contract.functions.swapExactTokensForTokens(
        0,
        0,
        [ETH_TOKEN_ADDRESS, USDC_TOKEN_ADDRESS],
        WALLET_ADDRESS
    ).build_transaction(transaction_params)
    return transaction

def send_transaction(provider, transaction):
    sign_transaction = provider.eth.account.sign_transaction(transaction, PRIVATE_KEY)
    send = provider.eth.send_raw_transaction()
    # return hash
    return provider.to_hex(send)

provider = connect()

if provider != False:
    print(get_gas_price(provider), 'Gwei')
    print(check_balance(provider), 'Eth')
    transaction = swap(provider)
    print(send_transaction(provider, transaction))
